changeSet.sql:
select identifier into @module_id from os_modules where name = 'biospecimen';
select identifier into @prop_id from os_cfg_props where name = 'mpi_format' and module_id = @module_id;
insert into os_cfg_settings (identifier, property_id, value, activated_by, activation_date, activity_status) values (default, @prop_id, 'B%06d', 1, current_timestamp(), 'Active');
